<?php Context::getInstance()->addHeaderFile('/modules/board/template/css/board.css', -1, 'head', NULL,NULL); ?><div class="board"><div class="board-article"><ul class="first-line"><li class="select-tag"><span><?php echo fetchLocale(array( 'en'=>'Category', 'kr'=>'말머리 선택')); ?></span><button class="select-tag-btn"></button></li><li class="title"><?php echo fetchLocale(array( 'en'=>'Title', 'kr'=>'제목')); ?></li><li class="writer"><?php echo fetchLocale(array( 'en'=>'Writer', 'kr'=>'작성자')); ?></li><li class="date"><?php echo fetchLocale(array( 'en'=>'Uploaded time', 'kr'=>'작성일')); ?></li><li class="hits"><?php echo fetchLocale(array( 'en'=>'Hits', 'kr'=>'조회수')); ?></li></ul><?php foreach ($__attr->articleData as $__attr->key =>$__attr->data): ?><ul class="<?php echo isset($__attr->data->top_notice) ? 'notice' : 'article' ?>"><?php if(isset($__attr->data->top_notice)) { ?><li class="notice-icon"><?php echo fetchLocale(array( 'en'=>'Notice', 'kr'=>'공지')); ?></li><?php } ?><li class="title"><?php if(!isset($__attr->data->top_notice)) { ?><?php if ($func = ModuleHandler::getModule('board')->view->printArticlePrefix($__attr->data->order_key, $__attr->data->category)) echo $func; ?><?php } ?><a href="http://127.0.0.1/intranet/<?php if (!USE_SHORT_URL) echo '?module=board&action=dispArticle&article_no='; ?><?php echo $__attr->data->no; ?>"><?php echo $__attr->data->title; ?></a></li><li class="writer"><?php echo $__attr->data->user_name; ?></li><li class="date"><?php echo $__attr->data->upload_time2; ?></li><li class="hits"><?php echo $__attr->data->hits; ?></li></ul><?php endforeach; ?><?php if(!$__attr->articleData) { ?><div class="no-article"><?php echo fetchLocale(array(
					'en'=>'There is no article',
					'kr'=>'게시글이 없습니다'
				)); ?></div><?php } ?></div><div class="page-numbers"><?php if($__attr->pageNumbers->prevBtn !== NULL) { ?><div class="prev-btn"><a href="http://127.0.0.1/intranet/modules/board/template/?page=<?php echo $__attr->pageNumbers->prevBtn; ?>&<?php echo $__attr->QS; ?>">&#9664;</a></div><?php } ?><?php foreach ($__attr->pageNumbers->pages as $__attr->key =>$__attr->n): ?><?php if($__attr->n != $__attr->nowPage) { ?><div class="page-btn"><a href="http://127.0.0.1/intranet/modules/board/template/?page=<?php echo $__attr->n; ?>&<?php echo $__attr->QS; ?>"><?php echo $__attr->n; ?></a></div><?php } ?><?php if($__attr->n == $__attr->nowPage) { ?><div class="page-btn current-page"><?php echo $__attr->n; ?></div><?php } ?><?php endforeach; ?><?php if($__attr->pageNumbers->nextBtn !== NULL) { ?><div class="next-btn"><a href="http://127.0.0.1/intranet/modules/board/template/?page=<?php echo $__attr->pageNumbers->nextBtn; ?>&<?php echo $__attr->QS; ?>">&#9654;</a></div><?php } ?></div></div>